def list_to_str(str_list):
    result = ""
    for s in str_list:
        result += str(s) + ""
    return result.strip()

# 소수점 아래 마지막 부터 0 제거
def delete_zero(str_list):
    for i in range(len(str_list)-1, -1, -1):
        if str_list[i] == "0":
            str_list.pop(i)
        else:
            break
def check_input_zero(str_list):
    idx = len(str_list) - 1
    check = 0
    while str_list[idx] == "0":
        check += 1
        idx -= 1
    return check

# '0' 과 '.' 을 제외한 str과 진법이 입력되면 곱셈 연산 실행.
def calculate_num(list_str, n, rs):
    try:
        result = []
        next_num = 0
        idx = len(list_str) - 1
        while idx >= 0:
            # 한 자리수와 곱셈 연산
            temp = int(list_str[idx]) * n + int(next_num)
            # 현재 자릿수의 결과를 result 배열에 저장
            result.append(str(temp % 10))
            # 다음 자릿수로 넘어갈 carry 계산
            next_num = str(temp // 10)
            idx -= 1

        if int(next_num) > 0:
            # next_num이 남아있다면 정수 부분 이므로 파라미터로 받은 rs에 추가
            rs.append(str(int(next_num) % 10))

        else:
            rs.append("0")


        # 결과를 역순으로 반환
        i = len(result) - 1
        while i >= 0:
            result.append(result.pop(i))
            i -= 1
    except:
        print("예외 발생")

    return result

# rs = 곱셈 후 추출된 정수 부분 리스트
# list_str = 곱셈 후 소수 부분 리스트
def convert_to_n_ary(in_str: str, to_ary: int) -> str:
    print(f"{in_str} \n을 {to_ary}진법으로 변환 시작:")
    # 문자열 배열로 저장
    list_str = list(in_str)
    # '0' 삭제
    list_str.pop(0)
    # '.' 삭제
    list_str.pop(0)

    rs = []
    while len(rs) != 1000:
        list_str = calculate_num(list_str, to_ary, rs)
        # print(f'list_str = {list_str}')

    if rs[len(rs) - 1] == "0":
        check = 0
        # 만약 소수점 아래 가장 뒤 부터 10개 연속으로 "0" 검출 시 0 삭제
        i = len(rs) - 1
        while rs[i] == "0" and i >= 0:
            check += 1
            print(f"check 0 = {check}개")
            i -= 1
            if check == 10:
                print("필요없는 0을 삭제합니다.")
                delete_zero(rs)
                break
    return f"0.{list_to_str(rs)}"

def equal_str(str1, str2):
    for i in range(len(str1)):
        if str1[i] != str2[i]:
            print(f'str1[{i}] : {str1[i]} != str2[{i}] : {str2[i]}')
            return
    print('두 문자열은 같습니다!')

